import React, { useState } from 'react'
import Blogcard from './Blogcard';
import { useEffect } from 'react';
import axios from 'axios';
import Navbars from '../navbar/Navbars';

const Allblog = () => {
    const [data, setdata] = useState([
        {
            title: '',
            description: '',
            imageurl: '',
        }
    ]);
    const [value, setvalue] = useState();

    const getData = () => {
        let token = localStorage.getItem('token');
        axios.get('http://localhost:3000/blog/find',{
            headers: { token: token }
        })
            .then(function (response) {
                // handle success
                console.log(response.data.data);
                setdata(response.data.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })
    }



    useEffect(() => {
        getData();
    }, [])


    const handelserch = (e) => {
        let token = localStorage.getItem('token');
        if (value) {
            axios.get("http://localhost:3000/search?search=" + value, {
                headers: { token: token }
            })
                .then(function (response) {
                    // handle success
                    console.log(response.data.data);
                    setdata(response.data.data);
                })
                .catch(function (error) {
                    // handle error
                    console.log(error);
                })
        }
        else{
            getData();
        }
    }


    return (
        <>
            <Navbars />

            <div className="container">
                <div className='mt-5'>
                    <form action="">
                        <input type="serch" className='mx-2' onChange={(e) => setvalue(e.target.value)} value={value} />
                        <button onClick={handelserch}>serch</button>
                    </form>

                </div>
                <div className="row">

                    {
                        data.map(
                            (el) => {
                                return (

                                    <>
                                        <div className="col-sm-12 col-md-4 col-lg-4">
                                            <Blogcard title={el.title} description={el.description} imageurl={el.imageurl} />
                                        </div>
                                    </>
                                )
                            }
                        )
                    }


                </div>

            </div>

        </>
    )
}

export default Allblog
