import React from 'react'
import { Formik, Field, Form } from 'formik';
import axios from 'axios';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';


const Loginform = () => {
    const history = useHistory();
    return (
        <>
            <div classNameName='container d-flex flex-column  justify-content-center align-items-center' style={{ marginTop: "100px" }}>

                <Formik
                    initialValues={{

                        email: '',
                        password: ''
                    }}
                    onSubmit={async (values) => {
                        axios.post('http://localhost:3000/login', values)
                            .then(function (response) {
                                // handle success
                                console.log(response);
                                localStorage.setItem('token', response.data.token)
                                history.push('/sineup')
                            })
                            .catch(function (error) {
                                // handle error
                                console.log(error);
                            })
                    }}
                >
                    <div style={{  height: "400px", padding: "25px 100px" }}>


                        <Form>

                            <div className="form-container">
                                <p className="title">Login</p>
                                <div className="form">

                                </div>
                                    
                         
                                <div className="social-message">
                                    <div className="line"></div>
                                    <p className="message d-flex ">Login with social accounts <a className='nav-link' onClick={() => history.push('/signup')}> Signup</a></p>
                                    <div className="line"></div>
                                </div>
                                <div className="social-icons">
                                    <button aria-label="Log in with Google" className="icon"></button>
                                </div>
                            </div>
                        </Form>
                    </div>
                </Formik>
            </div>
        </>
    )
}



export default Loginform
