import React from 'react'
import { Formik, Field, Form } from 'formik';
import axios from 'axios';
const Addblogform = () => {
    return (
        <>
            <div className='container d-flex flex-column mt-5 justify-content-center align-items-center'>
                <h1>add blog</h1>

                <Formik
                    initialValues={{
                        title: '',
                        description: '',
                        imageurl: '',

                    }}
                    onSubmit={async (values) => {
                        axios.post('http://localhost:3000/blog/create', values)
                            .then(function (response) {
                                // handle success
                                console.log(response);
                                localStorage.setItem('token', response.data.token)
                            })
                            .catch(function (error) {
                                // handle error
                                console.log(error);
                            })
                    }}
                >
                    <div  style={{backgroundColor:"grey", height:"500px" , padding:"100px 100px"}}>
                        <Form>
                            <label htmlFor="title" className='fs-4'>Blog title</label>
                            <br />
                            <Field id="title" className="px-2 py-2" name="title" placeholder="enter title" />
                            <br /><br />


                            <label htmlFor="description">Blog description</label>
                            <br />
                            <Field id="description" name="description" placeholder="enter blog description" />
                            <br /><br />
                            <label htmlFor="imageurl">imageurl</label>

                            <br />
                            <Field
                                id="imageurl"
                                name="imageurl"
                                placeholder="enter imageurl"
                                type="imageurl"
                            />
                            <br /><br />

                            <button type="submit">Submit</button>

                        </Form>
                    </div>
                </Formik>
            </div>
        </>
    )
}

export default Addblogform
