import React from 'react'
import { Formik, Field, Form } from 'formik';
import axios from 'axios';
const Loginform = () => {
    return (
        <>
            <div className='container d-flex flex-column  justify-content-center align-items-center' style={{ marginTop: "100px" }}>

                <Formik
                    initialValues={{

                        email: '',
                        password: ''
                    }}
                    onSubmit={async (values) => {
                        axios.post('http://localhost:3000/login', values)
                            .then(function (response) {
                                // handle success
                                console.log(response);
                                localStorage.setItem('token', response.data.token)
                            })
                            .catch(function (error) {
                                // handle error
                                console.log(error);
                            })
                    }}
                >
                    <div style={{ backgroundColor: "grey", height: "400px", padding: "25px 100px" }}>


                        <Form>

                            <div class="form-container">
                                <p class="title">Login</p>
                                <form class="form">
                                    <div class="input-group">
                                        <label htmlfor="email">Email</label>
                                        <input type="text" name="email" id="email" placeholder="enter email" />
                                    </div>
                                    <div class="input-group">
                                        <label for="password">Password</label>
                                        <input type="password" name="password" id="password" placeholder="enter password" />
                                        <div class="forgot">
                                            <a rel="noopener noreferrer" href="#">Forgot Password ?</a>
                                        </div>
                                    </div>
                                    <button type="submit" class="sign">Sign in</button>
                                </form>
                                <div class="social-message">
                                    <div class="line"></div>
                                    <p class="message">Login with social accounts</p>
                                    <div class="line"></div>
                                </div>
                                <div class="social-icons">
                                    <button aria-label="Log in with Google" class="icon"></button>
                                </div>
                            </div>
                        </Form>
                    </div>
                </Formik>
            </div>
        </>
    )
}



export default Loginform
