import React from 'react'
import { Formik, Field, Form } from 'formik';
import axios from 'axios';
const Addblogform = () => {
    const showdata = async 
    return (
        <>
            <div className='container d-flex flex-column  justify-content-center align-items-center' style={{marginTop:"100px"}}>
             

                <Formik
                    initialValues={{
                        title: '',
                        description: '',
                        imageurl: '',

                    }}
                    onSubmit={async (values) => {
                        axios.post('http://localhost:3000/blog/create', values)
                            .then(function (response) {
                                // handle success
                                console.log(response);
                                localStorage.setItem('token', response.data.token)
                            })
                            .catch(function (error) {
                                // handle error
                                console.log(error);
                            })
                    }}
                >
                    <div  style={{backgroundColor:"grey", height:"500px" , padding:"25px 100px"}}>
                    <h1>Add blog</h1>
                        <Form>
                            <label htmlFor="title" className='fs-4 text-light'>Blog title</label>
                            <br />
                            <Field id="title" className="px-2 py-2" name="title" placeholder="enter title" />
                            <br /><br />


                            <label htmlFor="description" className='fs-4 text-light'>Blog description</label>
                            <br />
                            <Field id="description" className="px-2 py-2 "  name="description" placeholder="enter blog description" />
                            <br /><br />

                            <label htmlFor="imageurl"  className='fs-4 text-light'>imageurl</label>
                            <br />
                            <Field
                                id="imageurl"
                                name="imageurl"
                                placeholder="enter imageurl"
                                type="imageurl"
                                className="px-2 py-2 " 
                            />
                            <br /><br />

                            <button type="submit" className="px-3 py-1 ">Submit</button>

                        </Form>
                    </div>
                </Formik>
            </div>
        </>
    )
}

export default Addblogform
