import React from 'react'
import { Formik, Field, Form } from 'formik';
import axios from 'axios';
const Loginform = () => {
    return (
        <>
            <div className='container d-flex flex-column  justify-content-center align-items-center' style={{ marginTop: "100px" }}>

                <Formik
                    initialValues={{

                        email: '',
                        password: ''
                    }}
                    onSubmit={async (values) => {
                        axios.post('http://localhost:3000/login', values)
                            .then(function (response) {
                                // handle success
                                console.log(response);
                                localStorage.setItem('token', response.data.token)
                            })
                            .catch(function (error) {
                                // handle error
                                console.log(error);
                            })
                    }}
                >
                    <div style={{ backgroundColor: "grey", height: "400px", padding: "25px 100px" }}>
                        <h1>Login </h1>

                        <Form>

                            <label htmlFor="email" className='fs-4 text-light'>Email</label>
                            <br />
                            <Field
                                id="email"
                                name="email"
                                placeholder="enter email"
                                type="email"
                                className="px-2 py-2 "
                            />
                            <br /><br />
                            <label htmlFor="password" className='fs-4 text-light'>Password</label>
                            <br />
                            <Field
                                id="password"
                                name="password"
                                placeholder="enter password"
                                type="password"
                                className="px-2 py-2 "

                            />
                            <br /><br />
                            <button type="submit" className="px-2 py-2 "  >Submit</button>

                        </Form>
                    </div>
                </Formik>
            </div>
        </>
    )
}



export default Loginform
