import React, { useState } from 'react'
import Blogcard from './Blogcard';
import { useEffect } from 'react';
import axios from 'axios';
import Navbars from '../navbar/Navbars';

const Allblog = () => {
    const [value, setvalue] = useState("");

    const [data, setdata] = useState([
        {
            title: '',
            description: '',
            imageurl: '',
        }
    ]);
   



   




    useEffect(() => {
        gatdata();
    }, [])


    const handelserch = (el) => {
        let token = localStorage.getItem('token');
        if (value) {
            axios.get("http://localhost:3001/blog/search?search=" + value, {
                headers: { token: token }
            })
                .then(function (response) {
                    // handle success
                    console.log(response.data.data);
                    setdata(response.data.data);

                })
                .catch(function (error) {
                    // handle error
                    console.log(error);
                })
        }
        else {
            gatdata();
        }
    }


    return (
        <>
            <Navbars />

            <div className="container">
          

                <div className="row">

                    {
                        data.map(
                            (el, index) => {
                                return (

                                    <>
                                        <div className="col-sm-12 col-md-4 col-lg-4">
                                            <Blogcard key={index} title={el.title} description={el.description} imageurl={el.imageurl} />
                                        </div>
                                    </>
                                )
                            }
                        )
                    }


                </div>
                <div className='mt-5 mb-2'>
                    
                        <input type="search" id='site-search' className='mx-2' onChange={(e) => setvalue(e.target.value)} value={value} />
                        <button onClick={handelserch}>serch</button>
                    

                </div>

            </div>

        </>
    )
}

export default Allblog
