import React from 'react';
import { Formik, Field, Form } from 'formik';
import axios from 'axios';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';

const Sineupform = () => {
    const history = useHistory();

    return (
        <>
            <div className='container d-flex flex-column  justify-content-center align-items-center' style={{ marginTop: "100px" }}>

                <Formik
                    initialValues={{
                        fristname: '',
                        lastname: '',
                        email: '',
                        password: ''
                    }}
                    onSubmit={async (values) => {
                        axios.post('http://localhost:3000/sineup', values)
                            .then(function (response) {
                                // handle success
                                console.log(response);
                                localStorage.setItem('token', response.data.token)
                            })
                            .catch(function (error) {
                                // handle error
                                console.log(error);
                            })
                    }}
                >
                    <div style={{ height: "570px", padding: "25px 100px" }}>
                      
                        <Form >
                
                           <div className="form">
                            
                           </div>
                                <p class="title">Register </p>
                                <p class="message">Signup now and get full access to our app. </p>
                                <div class="flex">
                                    <label>
                                        <input class="input" id='fristname' name='fristname' type="text" placeholder="" required="" />
                                        <span>Firstname</span>
                                    </label>

                                    <label>
                                        <input class="input" id='lastname' name='lastname' type="text" placeholder="" required="" />
                                        <span>Lastname</span>
                                    </label>
                                </div>

                                <label>
                                    <input class="input" type="email" id='email' name='email' placeholder="" required="" />
                                    <span>Email</span>
                                </label>

                                <label>
                                    <input class="input" type="password" name='password' id='password' placeholder="" required="" />
                                    <span>Password</span>
                                </label>

                                <button class="submit" type="submit" >Submit</button>
                                <p class="signin">Already have an acount ? <a href="#"  onClick={() => history.push('/login')}>Signin</a> </p>
                       


                           
                        </Form>
                    </div>

                </Formik>
            </div>
        </>
    )
}



export default Sineupform
